using System;
using System.Collections.Generic;
class Program
{
	static void Main(string[] args)
	{
	    long mod = 1000000007;
		string[] str = Console.ReadLine().Split();
		long N = long.Parse(str[0]);
		long K = long.Parse(str[1]);
		var Elist = enum_div(N);
		Elist.Sort((x,y)=>(int)x-(int)y);
		var Plist = PrimeFactors(N);
		Plist.Sort((x,y)=>(int)x-(int)y);
		var PAlist = new List<long[]>();
		long t = Plist[0];
		long ct = 1;
		for(var i=1;i<Plist.Count;i++){
			if(t!=Plist[i]){
				PAlist.Add(new long[2]{t,ct});
				t = Plist[i];
				ct = 1;
			} else {
				ct++;
			}
		}
		PAlist.Add(new long[2]{t,ct});
		long[]Pma = new long[PAlist.Count];
		long[]Qma = new long[PAlist.Count];
		long[]PQma = new long[PAlist.Count];
		for(var i=0;i<PAlist.Count;i++){
			long[] Pa = new long[PAlist[i][1]+1];
			long[] Qa = new long[PAlist[i][1]+1];
			long[] PQa = new long[PAlist[i][1]+1];
			for(var j=0;j<=PAlist[i][1];j++){
				Pa[j] = 1;
				Qa[j] = PAlist[i][1]+1;
				PQa[j] = Pa[j] * Mpow(Qa[j],mod-2,mod) % mod;
			}
			for(var k=1;k<K;k++){
			    for(var j=1;j<=PAlist[i][1];j++){
				    for(var h=0;h<j;h++){
					    long Pa0 = Pa[h];
					    long Qa0 = Qa[h];
					    long Pa1 = Pa[j];
					    long Qa1 = Qa[j]*(j+1);
					    Pa[h] = (Pa0*Qa1+Pa1*Qa0) % mod;
					    Qa[h] = (Qa0*Qa1) % mod;
						PQa[h] = PQa[h] + PQa[j] * Mpow(j+1,mod-2,mod) % mod;
    				}
				    Qa[j] = Qa[j]*(j+1);
					PQa[j] = PQa[j] * Mpow(j+1,mod-2,mod) % mod;
			    }
			}
			long Psum = Pa[0];
			long Qsum = Qa[0];
			long PQsum = PQa[0];
			for(var j=1;j<=PAlist[i][1];j++){
				long Pa0 = Psum;
				long Qa0 = Qsum;
				long PQa0 = PQsum;
				long Pn = 1;
				for(var a=0;a<j;a++){
				    Pn = Pn * PAlist[i][0] % mod;
				}
				Psum = (Pa0*Qa[j]+Pn*Pa[j]*Qa0) % mod;
				Qsum = (Qa0 * Qa[j]) % mod;
				PQsum = (PQsum + Pn*PQa[j]) % mod;
			}
			Pma[i] = Psum;
			Qma[i] = Qsum;
			PQma[i] = PQsum;
		}
		long Pm = Pma[0];
		long Qm = Qma[0];
		long PQm = PQma[0];
		for(var i=1;i<PAlist.Count;i++){
		    Pm *= Pma[i] % mod;
		    Qm *= Qma[i] % mod;
			PQm = PQm * PQma[i] % mod;
		}
	    Console.WriteLine(PQm);
	}

	public static List<long> PrimeFactors(long n){
		long i = 2;
		long tmp = n;
		var list = new List<long>();
		while(i*i<=n){
			if(tmp%i==0){
				tmp /= i;
				list.Add(i);
			} else {
				i++;
			}
		}
		if(tmp != 1){
			list.Add(tmp);
		}
		return list;
	}
	public static List<long> enum_div(long n){
		long tmp = n;
		var list = new List<long>();
		for(var i=1;i*i<=n;i++){
			if(n%i==0){
				list.Add(i);
				if(i*i!=n){
					list.Add(n/i);
				}
			}
		}
		return list;
	}
	public static long Mpow(long x,long n,long m)
	{
		long ans = 1;
		while(n > 0){
			if(n % 2 == 1){
				ans = ans * x % m;
				n -= 1;
			}
			x = x * x % m;
			n /= 2;
		}
		return ans;
	}
}
