﻿using System;
using System.IO;
using System.Text;

namespace Anadi_and_Domino
{
    internal class Program
    {
        private static readonly StreamReader reader = new StreamReader(Console.OpenStandardInput(1024*10), Encoding.ASCII, false, 1024*10);
        private static readonly StreamWriter writer = new StreamWriter(Console.OpenStandardOutput(1024*10), Encoding.ASCII, 1024*10);

        private static int[] p;
        private static int[] size1;
        private static int[] size2;

        private static void Main(string[] args)
        {
            writer.WriteLine(Solve(args));
            writer.Flush();
        }

        private static int Solve(string[] args)
        {
            int n = Next();
            int m = Next();

            p = new int[n];
            var t = new int[n];
            var mask = new int[n];
            size1 = new int[n];
            size2 = new int[n];
            for (int i = 0; i < n; i++)
            {
                p[i] = i;
                size1[i] = 1;
            }

            var mm = new Tuple<int, int>[m];
            for (int i = 0; i < m; i++)
            {
                int a = Next() - 1;
                int b = Next() - 1;
                mm[i] = new Tuple<int, int>(a, b);
                t[a]++;
                t[b]++;
                mask[a] |= (1 << b);
                mask[b] |= (1 << a);

                int pa = GP(a);
                int pb = GP(b);
                if (pa != pb)
                {
                    p[pa] = pb;
                    size2[pb] += 1 + size2[pa];
                    size1[pb] += size1[pa];
                }
                else
                {
                    size2[pa]++;
                }
            }

            int ans = 0;
            for (int i = 0; i < n; i++)
            {
                if (p[i] == i)
                {
                    if (size1[i] < 7)
                        ans += size2[i];
                    else
                    {
                        int min = 5;
                        foreach (var y in mm)
                        {
                            int r = t[y.Item1] + t[y.Item2] - 1 - (SparseBitcount(mask[y.Item1] | mask[y.Item2]) - 1);
                            min = Math.Min(min, Math.Max(0, r));
                        }

                        ans = size2[i] - min;
                        break;
                    }
                }
            }


            return ans;
        }

        private static int SparseBitcount(int n)
        {
            int count = 0;
            while (n != 0)
            {
                count++;
                n &= (n - 1);
            }
            return count;
        }


        private static int GP(int i)
        {
            if (i == p[i])
                return i;
            return p[i] = GP(p[i]);
        }

        private static int Next()
        {
            int c;
            int res = 0;
            do
            {
                c = reader.Read();
                if (c == -1)
                    return res;
            } while (c < '0' || c > '9');
            res = c - '0';
            while (true)
            {
                c = reader.Read();
                if (c < '0' || c > '9')
                    return res;
                res *= 10;
                res += c - '0';
            }
        }
    }
}
