using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace ConsoleApp5
{
    class Program
    {
        static Stack<bool> GetBinaryNumber(long number)
        {
            Stack<bool> result = new Stack<bool>();
            while (number != 0)
            {
                result.Push(number % 2 == 1);
                number /= 2;
            }
            return result;
        }
        static long FastDegree(long number, long pow)
        {
            long result = 1;
            Stack<bool> StackPow = GetBinaryNumber(pow);
            while (StackPow.Count != 0)
            {
                result *= result;
                result %= (int)(1e9 + 7);
                if (StackPow.Pop())
                {
                    result *= number;
                    result %= (int)(1e9 + 7);
                }
            }
            return result;
        }

        static int GetCountOne(int number)
        {
            int result = 0;
            while (number != 0)
            {
                if ((number & 1) == 1)
                    result++;
                number /= 2;
            }
            return result;
        }
        static void Main(string[] args)
        {
            string[] str = Console.ReadLine().Split();
            int n = int.Parse(str[0]);
            int m = int.Parse(str[1]);
            if (n < 7)
            {
                Console.WriteLine(m);
            }
            else
            {
                List<List<int>> G = new List<List<int>>();
                for (int i = 0; i < 7; i++)
                {
                    G.Add(new List<int>());
                }
                int temp = m;
                while(temp-- !=0)
                {
                    str = Console.ReadLine().Split();
                    int first = int.Parse(str[0]) - 1;
                    int second = int.Parse(str[1]) - 1;
                    G[first].Add(second);
                    G[second].Add(first);
                }
                int min = 6;
                for(int i =0; i<n; i++)
                {
                    if (G[i].Count < min)
                        min = G[i].Count;
                }
                if (min != 0)
                    min--;
                Console.WriteLine(m - min);
            }
        }
    }
}
