            {
                arr[i] = int.Parse(data[i]);
                if(arr[i] == 1)
                {
                    if(firstOne == -1) firstOne = i;
                    ++ones;
                }
            }

            int max = ones;
            for(int i = 0; i < n; ++i)
            {
                if(i < firstOne || i > firstOne + ones - 1) continue;
                max = Math.Max(max, ones - (i - firstOne + 1) + (arr[i] == 0 ? 1 : 0));
            }
            Console.WriteLine(max);
        }
