	static int GetNumberOfDepartedBuses(int intervalStart, int intervalEnd, int departureFrequency, int firstDeparture = 5*60, int lastDeparture = 23*60+59)
	{
		int numUntilStart = intervalStart >= firstDeparture	
			? (intervalStart - firstDeparture) / departureFrequency + 1	
