using System;
using System.IO;
using System.Text;

namespace Undoubtedly_Lucky_Numbers
{
    internal class Program
    {
        private static readonly StreamReader reader = new StreamReader(Console.OpenStandardInput(1024*10), Encoding.ASCII, false, 1024*10);
        private static readonly StreamWriter writer = new StreamWriter(Console.OpenStandardOutput(1024*10), Encoding.ASCII, 1024*10);

        private static void Main(string[] args)
        {
            string s = reader.ReadLine();
            int n = int.Parse(s);

            int count;
            if (n <= 100)
                count = n;
            else
            {
                count = 99;
                int d = 4;
                for (int le = 3; le < s.Length; le++)
                {
                    count += 9;
                    count += 9*(d-1);
                    count += 36*(2*d - 2);
                }

                int sl = s.Length;
                for (int i = 1; i < 10; i++)
                {
                    int k = i;
                    for (int j = 1; j < sl; j++)
                    {
                        k = k*10 + i;
                    }
                    if (k <= n)
                        count++;
                    for (int j = 0; j < 10; j++)
                    {
                        if (j == i)
                            continue;

                        for (int m = (1 << (sl-1)) - 2; m >= 0; m--)
                        {
                            int mask = m;
                            k = i;
                            for (int jj = 1; jj < sl; jj++)
                            {
                                k *= 10;
                                if ((mask & 1) == 1)
                                    k += i;
                                else k += j;
                                mask >>= 1;
                            }
                            if (k <= n)
                                count++;
                        }
                    }
                }
            }

            writer.WriteLine(count);
            writer.Flush();
        }
    }
}
