        {
            bruteForce();
        }

        private static void bruteForce()
        {
            var input = Console.ReadLine().Split(' ');
            var baloons = int.Parse(input[0]);
            var friends = int.Parse(input[1]);
            var colors = Console.ReadLine();
            string[] eachFriendBallon = new string[friends];
