                if(lastAllocation >= friends)
                    break;

                if (eachFriendBallon[friendIndex].IndexOf(colors[i]) == -1)
                {
                    eachFriendBallon[friendIndex] += colors[i];
                    lastAllocation = 0;;
                }
                else
                {
                    i--;
                    lastAllocation++;
                }

                friendIndex++;
            }

            var uniqueAllocated = 0;
            for (var j = 0; j < friends; j++)
            {
                uniqueAllocated += eachFriendBallon[j].Length;
            }

            if (uniqueAllocated == baloons)
            {
                Console.WriteLine("YES");
            }
            else
            {
                Console.WriteLine("NO");
            }
        }
    }
