using System;

class Program
{
    static void Main()
    {
        int n = int.Parse(Console.ReadLine());
        string flights = Console.ReadLine();
        int seattleToSanFrancisco = 0;
        int sanFranciscoToSeattle = 0;
        for (int i = 0; i < n; i++)
        {
            if (flights[i] == 'S')
                sanFranciscoToSeattle++;
            else
                seattleToSanFrancisco++;
        }
        Console.WriteLine(seattleToSanFrancisco > sanFranciscoToSeattle ? "YES" : "NO");
    }
}
