using static System.Linq.Enumerable;
class Solution {

  static void Main(string [] args) {
    var A = Array.ConvertAll<string, int>(Console.ReadLine().Split(' '), int.Parse);
    if (A.Sum() % 2 > 0) {
      Console.WriteLine("Impossible");
    }
    else {
      int[] ans = {0, 0, 0};
      while (A.Sum() > 0) {
        int argMin = A.ToList().IndexOf(A.Min());
        int i = (argMin + 1) % 3, j = (i + 1) % 3;
        if(A[i] > 0 && A[j] > 0){
            --A[i]; --A[j];
            ++ans[i];
        }
        else{
            Console.WriteLine("Impossible");
            return;
        }
      }
      for (int i = 0; i < 3; ++i) {
        Console.Write(ans[i].ToString() + " \n"[i == 2 ? 1 : 0]);
      }
    }
  }
};
