        static bool s_multiTest = false;
        static void Main(string[] args)
        {
            var o = Console.Out;
            var nTest = 1;
            if (s_multiTest)
            {
                nTest = NextInt();
            }

            for (int testId = 1; testId <= nTest; testId++)
            {
                Solve(testId, o);
            }
